````java path=src/main/java/com/example/component/MessageBubble.java mode=EDIT
// 在 MessageBubbleListener 接口中添加转发消息的方法
public interface MessageBubbleListener {
 /**
 * 当气泡被点击时调用
 * @param bubble 被点击的气泡
 */
 default void onBubbleClicked(MessageBubble bubble) {}

 /**
 * 当气泡被长按时调用
 * @param bubble 被长按的气泡
 */
 default void onBubbleLongPressed(MessageBubble bubble) {}

 /**
 * 当气泡被双击时调用
 * @param bubble 被双击的气泡
 */
 default void onBubbleDoubleClicked(MessageBubble bubble) {}

 /**
 * 当气泡内容被选中时调用
 * @param bubble 气泡
 * @param selectedText 选中的文本
 */
 default void onTextSelected(MessageBubble bubble, String selectedText) {}

 /**
 * 当消息被转发时调用
 * @param bubble 要转发的消息气泡
 */
 default void onForwardMessage(MessageBubble bubble) {}
}
