````java path=src/main/java/com/example/view/MainView.java mode=EDIT
// 添加处理转发消息的方法
private void handleForwardMessage(String content) {
 // 如果当前没有选中用户，显示用户选择对话框
 if (selectedUserId == null) {
 showForwardUserSelectionDialog(content);
 } else {
 // 直接转发给当前选中的用户
 forwardMessageToUser(selectedUserId, content);
 }
}

// 显示用户选择对话框
private void showForwardUserSelectionDialog(String content) {
 // 创建用户选择对话框
 JDialog dialog = new JDialog(this, "选择转发对象", true);
 dialog.setLayout(new BorderLayout());
 dialog.setSize(300, 400);
 dialog.setLocationRelativeTo(this);

 // 创建用户列表
 DefaultListModel<UserListItem> listModel = new DefaultListModel<>();
 for (Map.Entry<Long, String> entry : onlineUsers.entrySet()) {
 if (!entry.getKey().equals(currentUser.getId())) {
 listModel.addElement(new UserListItem(entry.getKey(), entry.getValue()));
 }
 }

 JList<UserListItem> userList = new JList<>(listModel);
 userList.setCellRenderer(new UserListCellRenderer());
 userList.setSelectionMode(ListSelectionModel.SINGLE_SELECTION);

 // 添加确认按钮
 JButton confirmButton = new JButton("转发");
 confirmButton.addActionListener(e -> {
 UserListItem selectedItem = userList.getSelectedValue();
 if (selectedItem != null) {
 forwardMessageToUser(selectedItem.getId(), content);
 dialog.dispose();
 }
 });

 // 添加取消按钮
 JButton cancelButton = new JButton("取消");
 cancelButton.addActionListener(e -> dialog.dispose());

 // 创建按钮面板
 JPanel buttonPanel = new JPanel(new FlowLayout(FlowLayout.RIGHT));
 buttonPanel.add(cancelButton);
 buttonPanel.add(confirmButton);

 // 添加组件到对话框
 dialog.add(new JScrollPane(userList), BorderLayout.CENTER);
 dialog.add(buttonPanel, BorderLayout.SOUTH);

 // 显示对话框
 dialog.setVisible(true);
}

// 转发消息给指定用户
private void forwardMessageToUser(Long userId, String content) {
 if (out != null) {
 // 发送转发消息给服务器
 out.println("FORWARD:" + userId + ":" + content);

 // 在本地显示转发成功提示
 chatPanel.addSystemMessage("已转发消息给 " + onlineUsers.get(userId));
 }
}

// 用户列表项类
private static class UserListItem {
 private final Long id;
 private final String name;

 public UserListItem(Long id, String name) {
 this.id = id;
 this.name = name;
 }

 public Long getId() {
 return id;
 }

 public String getName() {
 return name;
 }

 @Override
 public String toString() {
 return name;
 }
}

// 用户列表单元渲染器
private static class UserListCellRenderer extends DefaultListCellRenderer {
 @Override
 public Component getListCellRendererComponent(JList<?> list, Object value, int index, boolean isSelected, boolean cellHasFocus) {
 JLabel label = (JLabel) super.getListCellRendererComponent(list, value, index, isSelected, cellHasFocus);

 if (value instanceof UserListItem) {
 UserListItem item = (UserListItem) value;
 label.setText(item.getName());
 }

 return label;
 }
}
````

最后，我们需要在 `SocketService` 类中添加处理转发消息的逻辑：

````java path=src/main/java/com/example/service/SocketService.java mode=EDIT
// 在处理客户端消息的方法中添加转发消息的处理
private void handleClientMessage(long senderId, String message) {
 if (message.startsWith("FORWARD:")) {
 // 处理转发消息
 String[] parts = message.substring(8).split(":", 2);
 if (parts.length == 2) {
 long targetUserId = Long.parseLong(parts[0]);
 String content = parts[1];

 // 转发消息给目标用户
 String formattedMessage = "User" + senderId + " (转发): " + content;
 sendMessage(targetUserId, formattedMessage);
 }
 } else {
 // 处理普通消息
 broadcastMessage(senderId, message);
 }
}
````

