package com.example.view;

import com.example.component.ChatPanel;
import com.example.model.User;
import com.sun.javafx.collections.MappingChange;

import javax.swing.*;
import java.awt.*;
import java.awt.event.WindowAdapter;
import java.awt.event.WindowEvent;
import java.io.*;
import java.net.Socket;
import javax.swing.event.DocumentEvent;
import javax.swing.event.DocumentListener;
import javax.swing.plaf.basic.BasicScrollBarUI;
import java.util.HashMap;
import java.util.Map;

public class MainView extends JFrame {
 // 所有成员变量和方法...

 // 确保toggleUserList方法是类中的最后一个方法，并且正确闭合
 private void toggleUserList() {
 showingUserList = !showingUserList;

 // 从布局中移除当前显示的面板
 remove(showingUserList ? privateChatListPanel : userListPanel);

 // 更新标题和按钮文本
 JLabel titleLabel = new JLabel(showingUserList ? "在线用户" : "私聊列表");
 titleLabel.setFont(CHINESE_FONT_BOLD);
 titleLabel.setForeground(TEXT_COLOR);

 switchListButton.setText(showingUserList ? "切换到私聊" : "切换到在线");

 // 添加新的面板
 add(showingUserList ? userListPanel : privateChatListPanel, BorderLayout.WEST);

 // 刷新UI
 revalidate();
 repaint();
 }

 // 这是类的结束括号，确保没有其他代码在这之后
}
```
