 public MainView(User user) {
 this.currentUser = user;
 initUI();
 connectToServer();
 startMessageListening();

 // 添加窗口关闭监听器以强制结束进程
 addWindowListener(new WindowAdapter() {
 @Override
 public void windowClosing(WindowEvent e) {
 System.out.println("强制结束进程...");

 // 打印所有活动线程
 System.out.println("当前活动线程:");
 Thread.getAllStackTraces().keySet().forEach(thread -> {
 System.out.println(thread.getName() + " - 守护线程: " + thread.isDaemon() + 
 " - 状态: " + thread.getState());
 });

 // 尝试关闭连接，但不等待
 try {
 closeConnection();
 } catch (Exception ex) {
 ex.printStackTrace();
 } finally {
 // 先尝试正常关闭
 dispose();
 // 然后强制结束进程 - 使用最直接的方式
 System.out.println("执行强制终止...");
 Runtime.getRuntime().halt(0);
 }
 }
 });
 }

 private void initUI() {
 // 设置现代化外观
 try {
 UIManager.setLookAndFeel(UIManager.getSystemLookAndFeelClassName());
 } catch (Exception e) {
 e.printStackTrace();
 }

 setTitle("通信软件 - " + currentUser.getName());
 setSize(900, 700);
 // 设置为DO_NOTHING_ON_CLOSE，让我们的windowClosing事件处理器完全控制关闭行为
 setDefaultCloseOperation(JFrame.DO_NOTHING_ON_CLOSE);
 setLocationRelativeTo(null);
 setLayout(new BorderLayout());
 getContentPane().setBackground(BACKGROUND_COLOR);

 // 添加现代化标题栏
 initTitleBar();
 initChatPanel();
 initInputPanel();
 initUserListPanel();

 add(userListPanel, BorderLayout.WEST);
 add(scrollPane, BorderLayout.CENTER);
 add(inputPanel, BorderLayout.SOUTH);

 // 确保输入框可用
 SwingUtilities.invokeLater(() -> inputField.requestFocusInWindow());
 }

