 hostField.setFont(CHINESE_FONT);
 hostField.setPreferredSize(new Dimension(200, 30)); // 设置固定大小
 hostPanel.add(hostField);
 mainPanel.add(hostPanel);
 mainPanel.add(Box.createVerticalStrut(15));

 // 端口设置
