import com.example.service.AIService;
import java.awt.event.MouseAdapter;
import java.awt.event.MouseEvent;
import javax.swing.SwingWorker;

/**
 * 创建文本右键菜单
 * @param textArea 文本区域
 * @return 右键菜单
 */
private JPopupMenu createTextPopupMenu(JTextArea textArea) {
 JPopupMenu popupMenu = new JPopupMenu();

 // 复制选中文本
 JMenuItem copyItem = new JMenuItem("复制");
 copyItem.addActionListener(e -> {
 textArea.copy();
 });
 popupMenu.add(copyItem);

 // 复制全部文本
 JMenuItem copyAllItem = new JMenuItem("复制全部");
 copyAllItem.addActionListener(e -> {
 textArea.selectAll();
 textArea.copy();
 });
 popupMenu.add(copyAllItem);

 // 分隔线
 popupMenu.addSeparator();

 // 翻译文本
 JMenuItem translateItem = new JMenuItem("翻译");
 translateItem.addActionListener(e -> {
 translateBubbleContent();
 });
 popupMenu.add(translateItem);

 // 分隔线
 popupMenu.addSeparator();

 // 选择全部
 JMenuItem selectAllItem = new JMenuItem("全选");
 selectAllItem.addActionListener(e -> {
 textArea.selectAll();
 });
 popupMenu.add(selectAllItem);

 return popupMenu;
}

/**
 * 翻译气泡内容
 */
public void translateBubbleContent() {
 // 如果已经触发了翻译，不再重复翻译
 if (getClientProperty("translating") != null && (boolean) getClientProperty("translating")) {
 return;
 }

 // 标记正在翻译
 putClientProperty("translating", true);

 // 创建一个SwingWorker来执行翻译，避免阻塞UI线程
 new SwingWorker<String, Void>() {
 @Override
 protected String doInBackground() throws Exception {
 // 使用AIService翻译文本
 AIService aiService = new AIService();
 return aiService.translateText(content, "zh-CN");
 }

 @Override
 protected void done() {
 try {
 String translatedText = get();
 if (translatedText != null && !translatedText.isEmpty()) {
 // 创建翻译结果气泡
 createTranslationBubble(translatedText);
 }
 } catch (Exception e) {
 System.err.println("翻译出错: " + e.getMessage());
 } finally {
 // 标记翻译完成
 putClientProperty("translating", false);
 }
 }
 }.execute();
}

/**
 * 创建翻译结果气泡
 * @param translatedText 翻译后的文本
 */
private void createTranslationBubble(String translatedText) {
 // 获取当前气泡所在的容器
 Container parent = getParent();
 if (parent == null) return;

 // 创建翻译气泡 - 使用浅色调
 Color translationBubbleColor = isOwnMessage ? 
 new Color(bubbleColor.getRed(), bubbleColor.getGreen(), bubbleColor.getBlue(), 200) : 
 new Color(230, 230, 230);

 MessageBubble translationBubble = new MessageBubble(
 translatedText,
 isOwnMessage,
 translationBubbleColor,
 textColor,
 new Font(textFont.getName(), Font.ITALIC, textFont.getSize() - 1)
 );

 // 添加标识，防止翻译气泡再次被翻译
 translationBubble.putClientProperty("isTranslation", true);

 // 获取当前气泡在父容器中的索引
 int index = -1;
 Component[] components = parent.getComponents();
 for (int i = 0; i < components.length; i++) {
 if (components[i] == this) {
 index = i;
 break;
 }
 }

 // 如果找到索引，在当前气泡后添加翻译气泡
 if (index != -1 && parent instanceof JPanel) {
 JPanel panel = (JPanel) parent;

 // 检查是否已经有翻译气泡
 boolean hasTranslation = false;
 if (index + 1 < components.length) {
 Component nextComponent = components[index + 1];
 if (nextComponent instanceof MessageBubble && 
 ((MessageBubble) nextComponent).getClientProperty("isTranslation") != null) {
 // 替换现有的翻译气泡
 panel.remove(nextComponent);
 panel.add(translationBubble, index + 1);
 hasTranslation = true;
 }
 }

 // 如果没有现有的翻译气泡，添加新的
 if (!hasTranslation) {
 panel.add(translationBubble, index + 1);
 }

 // 重新布局
 panel.revalidate();
 panel.repaint();

 // 如果在ChatPanel中，滚动到底部
 Container topContainer = SwingUtilities.getAncestorOfClass(ChatPanel.class, panel);
 if (topContainer instanceof ChatPanel) {
 ((ChatPanel) topContainer).scrollToBottom();
 }
 }
}
```
</augment_code_snippet>

现在，让我们修改 `AIService` 类，完善翻译功能：

<augment_code_snippet path="src/main/java/com/example/service/AIService.java" mode="EDIT">
```java
package com.example.service;

import com.example.model.Settings;
import okhttp3.*;
import org.json.JSONObject;

import java.io.IOException;
import java.util.concurrent.TimeUnit;

public class AIService {
 private static final String API_URL = "https://api.siliconflow.cn/v1/chat/completions";
 private static final int MAX_TOKENS = 1000;
 private final OkHttpClient httpClient;
 private final Settings settings;

 public AIService() {
 // 创建带超时设置的HTTP客户端
 this.httpClient = new OkHttpClient.Builder()
 .connectTimeout(10, TimeUnit.SECONDS)
 .readTimeout(30, TimeUnit.SECONDS)
 .writeTimeout(30, TimeUnit.SECONDS)
 .build();

 this.settings = Settings.getInstance();
 }

 /**
 * 翻译文本
 * @param text 要翻译的文本
 * @param targetLang 目标语言代码 (如 "zh-CN", "en-US")
 * @return 翻译后的文本
 */
 public String translateText(String text, String targetLang) {
 try {
 // 构建请求JSON
 JSONObject requestJson = new JSONObject();
 requestJson.put("model", "gpt-3.5-turbo");

 // 添加系统消息和用户消息
 JSONObject systemMessage = new JSONObject();
 systemMessage.put("role", "system");
 systemMessage.put("content", "You are a translator. Translate the text to " + getLanguageName(targetLang) + 
 ". Only return the translated text without any explanations or additional text.");

 JSONObject userMessage = new JSONObject();
 userMessage.put("role", "user");
 userMessage.put("content", text);

 requestJson.append("messages", systemMessage);
 requestJson.append("messages", userMessage);

 // 设置最大令牌数
 requestJson.put("max_tokens", MAX_TOKENS);

 // 创建请求体
 RequestBody body = RequestBody.create(
 MediaType.parse("application/json"), requestJson.toString());

 // 从配置中获取API密钥
 String apiKey = settings.getProperty("ai.api.key", "");

 // 构建请求
 Request request = new Request.Builder()
 .url(API_URL)
 .post(body)
 .header("Content-Type", "application/json")
 .header("Authorization", "Bearer " + apiKey)
 .build();

 // 执行请求
 try (Response response = httpClient.newCall(request).execute()) {
 if (!response.isSuccessful()) {
 return "翻译错误: " + response.code();
 }

 // 解析响应
 String responseBody = response.body().string();
 JSONObject jsonResponse = new JSONObject(responseBody);

 // 提取翻译结果
 String translatedText = jsonResponse
 .getJSONArray("choices")
 .getJSONObject(0)
 .getJSONObject("message")
 .getString("content");

 return translatedText.trim();
 }
 } catch (IOException e) {
 return "翻译服务不可用: " + e.getMessage();
 } catch (Exception e) {
 return "翻译过程中出错: " + e.getMessage();
 }
 }

 /**
 * 根据语言代码获取语言名称
 * @param langCode 语言代码
 * @return 语言名称
 */
 private String getLanguageName(String langCode) {
 switch (langCode.toLowerCase()) {
 case "zh-cn":
 return "Chinese (Simplified)";
 case "en-us":
 case "en":
 return "English";
 case "ja":
 return "Japanese";
 case "ko":
 return "Korean";
 case "fr":
 return "French";
 case "de":
 return "German";
 case "es":
 return "Spanish";
 case "ru":
 return "Russian";
 default:
 return langCode;
 }
 }
}
```
</augment_code_snippet>

