// Update loadAllUsers method to use UserDAO
private void loadAllUsers() {
 SwingWorker<List<User>, Void> worker = new SwingWorker<List<User>, Void>() {
 @Override
 protected List<User> doInBackground() throws Exception {
 // Get all users from database
 List<User> users = userDAO.getAllUsers();

 // Make sure current user is in the list
 boolean currentUserFound = false;
 for (User user : users) {
 if (user.getId() == currentUser.getId()) {
 currentUserFound = true;
 break;
 }
 }

 if (!currentUserFound) {
 users.add(currentUser);
 }

 return users;
 }

 @Override
 protected void done() {
 try {
 allUsers = get();
 // Mark current user as online
 for (User user : allUsers) {
 if (user.getId() == currentUser.getId()) {
 user.setOnline(true);
 break;
 }
 }

 // If we're showing all users, update the list
 if (showingAllUsers) {
 updateUserListWithAllUsers();
 } else {
 updateUserListWithOnlineUsers();
 }
 } catch (Exception e) {
 e.printStackTrace();
 }
 }
 };

 worker.execute();
}

