startServerButton = new JButton("启动服务器");
startServerButton.setFont(CHINESE_FONT);
startServerButton.setForeground(PRIMARY_COLOR);
startServerButton.setBorderPainted(false);
startServerButton.setContentAreaFilled(false);
startServerButton.setFocusPainted(false);
