````java path=src/main/java/com/example/view/SettingsView.java mode=EDIT
public SettingsView() {
 settings = Settings.getInstance();
 socketService = new SocketService();

 // 检查服务器是否已经在运行
 isServerRunning = settings.isStartServerMode();

 initUI();
}
