````java path=src/main/java/com/example/view/MainView.java mode=EDIT
private JLabel createUserAvatar(String name) {
 String initial = getInitials(name);

 JLabel avatar = new JLabel(initial, SwingConstants.CENTER);
 avatar.setPreferredSize(new Dimension(40, 40));
 avatar.setFont(new Font("微软雅黑", Font.BOLD, 16));
 avatar.setForeground(Color.WHITE);
 avatar.setOpaque(true);
 avatar.setBackground(PRIMARY_COLOR);

 // 使用圆形头像
 avatar.setBorder(new AbstractBorder() {
 @Override
 public void paintBorder(Component c, Graphics g, int x, int y, int width, int height) {
 Graphics2D g2 = (Graphics2D) g.create();
 g2.setRenderingHint(RenderingHints.KEY_ANTIALIASING, RenderingHints.VALUE_ANTIALIAS_ON);
 g2.setColor(PRIMARY_COLOR);
 g2.fillOval(x, y, width, height);
 g2.dispose();
 }

 @Override
 public Insets getBorderInsets(Component c) {
 return new Insets(0, 0, 0, 0);
 }

 @Override
 public boolean isBorderOpaque() {
 return true;
 }
 });

 return avatar;
}
