````java path=src/main/java/com/example/component/MessageBubble.java mode=EDIT
/**
 * 创建翻译结果气泡
 * @param translatedText 翻译后的文本
 */
private void createTranslationBubble(String translatedText) {
 // 获取当前气泡所在的容器
 Container parent = getParent();
 if (parent == null) return;

 // 创建翻译气泡 - 使用浅色调
 Color translationBubbleColor = isOwnMessage ? 
 new Color(bubbleColor.getRed(), bubbleColor.getGreen(), bubbleColor.getBlue(), 200) : 
 new Color(230, 230, 230);

 MessageBubble translationBubble = new MessageBubble(
 translatedText,
 isOwnMessage,
 translationBubbleColor,
 textColor,
 new Font(textFont.getName(), Font.ITALIC, textFont.getSize() - 1)
 );

 // 添加标识，防止翻译气泡再次被翻译
 translationBubble.putClientProperty("isTranslation", true);

 // 获取当前气泡在父容器中的索引
 int index = -1;
 Component[] components = parent.getComponents();
 for (int i = 0; i < components.length; i++) {
 if (components[i] == this) {
 index = i;
 break;
 }
 }

 // 如果找到索引，在当前气泡后添加翻译气泡
 if (index != -1) {
 // 获取当前气泡所在的消息行面板
 Container messageRow = parent;
 Container wrapperPanel = messageRow.getParent();

 // 创建新的消息行面板，用于放置翻译气泡
 JPanel translationRow = new JPanel(new FlowLayout(isOwnMessage ? FlowLayout.RIGHT : FlowLayout.LEFT, 5, 0));
 translationRow.setOpaque(false);

 // 添加翻译气泡到新的消息行
 translationRow.add(translationBubble);

 // 创建一个包装面板，用于控制对齐
 JPanel translationWrapper = new JPanel(new BorderLayout());
 translationWrapper.setOpaque(false);

 // 根据消息类型添加到不同位置
 if (isOwnMessage) {
 translationWrapper.add(translationRow, BorderLayout.EAST);
 } else {
 translationWrapper.add(translationRow, BorderLayout.WEST);
 }

 // 获取ChatPanel
 Container chatPanel = SwingUtilities.getAncestorOfClass(ChatPanel.class, wrapperPanel);
 if (chatPanel instanceof ChatPanel) {
 // 获取内容面板
 JPanel contentPanel = ((ChatPanel) chatPanel).getContentPanel();

 // 检查是否已经有翻译气泡
 boolean hasTranslation = false;
 int wrapperIndex = -1;
 Component[] wrapperComponents = contentPanel.getComponents();
 for (int i = 0; i < wrapperComponents.length; i++) {
 if (wrapperComponents[i] == wrapperPanel) {
 wrapperIndex = i;
 // 检查下一个组件是否是翻译气泡的包装面板
 if (i + 1 < wrapperComponents.length) {
 Component nextComponent = wrapperComponents[i + 1];
 if (nextComponent instanceof JPanel) {
 Component[] nextComponents = ((JPanel) nextComponent).getComponents();
 for (Component comp : nextComponents) {
 if (comp instanceof JPanel) {
 Component[] panelComps = ((JPanel) comp).getComponents();
 for (Component panelComp : panelComps) {
 if (panelComp instanceof MessageBubble && 
 ((MessageBubble) panelComp).getClientProperty("isTranslation") != null) {
 hasTranslation = true;
 contentPanel.remove(nextComponent);
 break;
 }
 }
 }
 if (hasTranslation) break;
 }
 }
 }
 break;
 }
 }

 // 如果找到索引，在当前气泡后添加翻译气泡
 if (wrapperIndex != -1) {
 contentPanel.add(translationWrapper, wrapperIndex + 1);
 contentPanel.revalidate();
 contentPanel.repaint();

 // 滚动到底部
 ((ChatPanel) chatPanel).scrollToBottom();
 }
 }
 }
}
