 @Override
 protected String doInBackground() throws Exception {
 try {
 // 使用AIService翻译文本
 AIService aiService = new AIService();
 System.out.println("开始翻译文本: " + content);
 String result = aiService.translateText(content, "zh-CN");
 System.out.println("翻译结果: " + result);
 return result;
 } catch (Exception e) {
 System.err.println("翻译出错: " + e.getMessage());
 e.printStackTrace();
 return "翻译失败: " + e.getMessage();
 }
 }

 @Override
 protected void done() {
 try {
 // 移除加载指示器
 if (parent != null) {
 parent.remove(loadingLabel);
 parent.revalidate();
 parent.repaint();
 System.out.println("移除了加载指示器");
 }

 String translatedText = get();
 System.out.println("获取到翻译结果: " + translatedText);

 if (translatedText != null && !translatedText.isEmpty()) {
 // 创建翻译结果气泡
 System.out.println("准备显示翻译结果");

 // 直接使用ChatPanel的方法添加翻译气泡
 Container chatPanel = SwingUtilities.getAncestorOfClass(ChatPanel.class, MessageBubble.this);
 if (chatPanel instanceof ChatPanel) {
 System.out.println("找到ChatPanel，添加翻译气泡");
 ((ChatPanel) chatPanel).addTranslationBubble(MessageBubble.this, translatedText);
 } else {
 System.err.println("未找到ChatPanel，尝试使用createTranslationBubble方法");
 createTranslationBubble(translatedText);
 }
 } else {
 System.err.println("翻译结果为空");
 JOptionPane.showMessageDialog(null, "翻译失败: 结果为空", "翻译错误", JOptionPane.ERROR_MESSAGE);
 }
 } catch (Exception e) {
 System.err.println("处理翻译结果时出错: " + e.getMessage());
 e.printStackTrace();
 JOptionPane.showMessageDialog(null, "翻译失败: " + e.getMessage(), "翻译错误", JOptionPane.ERROR_MESSAGE);
 } finally {
 // 标记翻译完成
 putClientProperty("translating", false);
 System.out.println("翻译过程完成");
 }
 }
 }.execute();
