````java path=src/main/java/com/example/component/MessageBubble.java mode=EDIT
/**
 * 消息气泡监听器接口
 */
public interface MessageBubbleListener {
 /**
 * 当气泡被点击时调用
 * @param bubble 被点击的气泡
 */
 default void onBubbleClicked(MessageBubble bubble) {}

 /**
 * 当气泡被长按时调用
 * @param bubble 被长按的气泡
 */
 default void onBubbleLongPressed(MessageBubble bubble) {}

 /**
 * 当气泡被双击时调用
 * @param bubble 被双击的气泡
 */
 default void onBubbleDoubleClicked(MessageBubble bubble) {}

 /**
 * 当气泡内容被选中时调用
 * @param bubble 气泡
 * @param selectedText 选中的文本
 */
 default void onTextSelected(MessageBubble bubble, String selectedText) {}

 /**
 * 当气泡内容被转发时调用
 * @param bubble 气泡
 * @param content 要转发的内容
 */
 default void onBubbleForwarded(MessageBubble bubble, String content) {}
}

/**
 * 创建文本右键菜单
 * @param textArea 文本区域
 * @return 右键菜单
 */
private JPopupMenu createTextPopupMenu(JTextArea textArea) {
 JPopupMenu popupMenu = new JPopupMenu();

 // 复制选中文本
 JMenuItem copyItem = new JMenuItem("复制");
 copyItem.addActionListener(e -> {
 textArea.copy();
 });
 popupMenu.add(copyItem);

 // 复制全部文本
 JMenuItem copyAllItem = new JMenuItem("复制全部");
 copyAllItem.addActionListener(e -> {
 textArea.selectAll();
 textArea.copy();
 });
 popupMenu.add(copyAllItem);

 // 分隔线
 popupMenu.addSeparator();

 // 转发消息
 JMenuItem forwardItem = new JMenuItem("转发");
 forwardItem.addActionListener(e -> {
 forwardBubbleContent();
 });
 popupMenu.add(forwardItem);

 // 翻译文本
 JMenuItem translateItem = new JMenuItem("翻译");
 translateItem.addActionListener(e -> {
 translateBubbleContent();
 });
 popupMenu.add(translateItem);

 // 分隔线
 popupMenu.addSeparator();

 // 选择全部
 JMenuItem selectAllItem = new JMenuItem("全选");
 selectAllItem.addActionListener(e -> {
 textArea.selectAll();
 });
 popupMenu.add(selectAllItem);

 return popupMenu;
}

/**
 * 转发气泡内容
 */
public void forwardBubbleContent() {
 if (bubbleListener != null) {
 bubbleListener.onBubbleForwarded(this, content);
 } else {
 // 如果没有设置监听器，显示转发对话框
 showForwardDialog();
 }
}

/**
 * 显示转发对话框
 */
private void showForwardDialog() {
 // 创建用户选择对话框
 JDialog forwardDialog = new JDialog((Frame)SwingUtilities.getWindowAncestor(this), "转发消息", true);
 forwardDialog.setLayout(new BorderLayout());
 forwardDialog.setSize(300, 400);
 forwardDialog.setLocationRelativeTo(this);

 // 创建用户列表面板
 JPanel userListPanel = new JPanel();
 userListPanel.setLayout(new BoxLayout(userListPanel, BoxLayout.Y_AXIS));
 userListPanel.setBorder(BorderFactory.createEmptyBorder(10, 10, 10, 10));

 JLabel titleLabel = new JLabel("选择转发对象:");
 titleLabel.setFont(new Font(textFont.getName(), Font.BOLD, textFont.getSize()));
 titleLabel.setAlignmentX(Component.LEFT_ALIGNMENT);
 userListPanel.add(titleLabel);
 userListPanel.add(Box.createVerticalStrut(10));

 // 这里应该添加用户列表，但由于我们没有直接访问用户列表，
 // 所以这部分需要通过监听器来实现
 JLabel placeholderLabel = new JLabel("请设置监听器以获取用户列表");
 placeholderLabel.setAlignmentX(Component.LEFT_ALIGNMENT);
 userListPanel.add(placeholderLabel);

 // 添加滚动面板
 JScrollPane scrollPane = new JScrollPane(userListPanel);
 forwardDialog.add(scrollPane, BorderLayout.CENTER);

 // 添加按钮面板
 JPanel buttonPanel = new JPanel(new FlowLayout(FlowLayout.RIGHT));
 JButton cancelButton = new JButton("取消");
 cancelButton.addActionListener(e -> forwardDialog.dispose());
 buttonPanel.add(cancelButton);

 forwardDialog.add(buttonPanel, BorderLayout.SOUTH);

 // 显示对话框
 forwardDialog.setVisible(true);
}
````

现在，让我们在 `ChatPanel` 类中添加对转发功能的支持：

````java path=src/main/java/com/example/component/ChatPanel.java mode=EDIT
/**
 * 创建消息气泡
 * @param content 消息内容
 * @param isOwnMessage 是否为自己发送的消息
 * @return 消息气泡面板
 */
private JPanel createMessageBubble(String content, boolean isOwnMessage) {
 // 使用MessageBubble类创建气泡
 MessageBubble bubble = new MessageBubble(
 content, 
 isOwnMessage, 
 isOwnMessage ? sentBubbleColor : receivedBubbleColor,
 isOwnMessage ? Color.WHITE : textColor,
 textFont
 );

 // 设置气泡监听器
 bubble.setMessageBubbleListener(new MessageBubble.MessageBubbleListener() {
 @Override
 public void onBubbleClicked(MessageBubble bubble) {
 // 可以在这里处理气泡点击事件
 }

 @Override
 public void onBubbleLongPressed(MessageBubble bubble) {
 // 可以在这里处理气泡长按事件
 }

 @Override
 public void onBubbleDoubleClicked(MessageBubble bubble) {
 // 可以在这里处理气泡双击事件
 }

 @Override
 public void onTextSelected(MessageBubble bubble, String selectedText) {
 // 可以在这里处理文本选中事件
 }

 @Override
 public void onBubbleForwarded(MessageBubble bubble, String content) {
 // 处理转发事件
 if (forwardListener != null) {
 forwardListener.onMessageForward(content, bubble.isOwnMessage());
 }
 }
 });

 return bubble;
}

// 转发监听器
private ForwardMessageListener forwardListener;

/**
 * 设置转发消息监听器
 * @param listener 转发消息监听器
 */
public void setForwardMessageListener(ForwardMessageListener listener) {
 this.forwardListener = listener;
}

/**
 * 转发消息监听器接口
 */
public interface ForwardMessageListener {
 /**
 * 当消息被转发时调用
 * @param content 消息内容
 * @param isOwnMessage 是否是自己的消息
 */
 void onMessageForward(String content, boolean isOwnMessage);
}

/**
 * 显示转发对话框
 * @param content 要转发的内容
 * @param userList 用户列表
 * @param forwardCallback 转发回调
 */
public void showForwardDialog(String content, List<User> userList, BiConsumer<String, User> forwardCallback) {
 // 创建用户选择对话框
 JDialog forwardDialog = new JDialog(SwingUtilities.getWindowAncestor(this), "转发消息", true);
 forwardDialog.setLayout(new BorderLayout());
 forwardDialog.setSize(300, 400);
 forwardDialog.setLocationRelativeTo(this);

 // 创建消息预览面板
 JPanel previewPanel = new JPanel(new BorderLayout());
 previewPanel.setBorder(BorderFactory.createCompoundBorder(
 BorderFactory.createMatteBorder(0, 0, 1, 0, Color.LIGHT_GRAY),
 BorderFactory.createEmptyBorder(10, 10, 10, 10)
 ));

 JLabel previewLabel = new JLabel("消息预览:");
 previewLabel.setFont(new Font(textFont.getName(), Font.BOLD, textFont.getSize()));
 previewPanel.add(previewLabel, BorderLayout.NORTH);

 JTextArea previewText = new JTextArea(content);
 previewText.setEditable(false);
 previewText.setLineWrap(true);
 previewText.setWrapStyleWord(true);
 previewText.setFont(textFont);
 previewText.setRows(3);
 previewText.setBorder(BorderFactory.createEmptyBorder(5, 0, 0, 0));
 previewPanel.add(new JScrollPane(previewText), BorderLayout.CENTER);

 forwardDialog.add(previewPanel, BorderLayout.NORTH);

 // 创建用户列表面板
 JPanel userListPanel = new JPanel();
 userListPanel.setLayout(new BoxLayout(userListPanel, BoxLayout.Y_AXIS));
 userListPanel.setBorder(BorderFactory.createEmptyBorder(10, 10, 10, 10));

 JLabel titleLabel = new JLabel("选择转发对象:");
 titleLabel.setFont(new Font(textFont.getName(), Font.BOLD, textFont.getSize()));
 titleLabel.setAlignmentX(Component.LEFT_ALIGNMENT);
 userListPanel.add(titleLabel);
 userListPanel.add(Box.createVerticalStrut(10));

 // 添加用户列表
 if (userList != null && !userList.isEmpty()) {
 for (User user : userList) {
 JPanel userItem = createUserItem(user, content, forwardCallback, forwardDialog);
 userItem.setAlignmentX(Component.LEFT_ALIGNMENT);
 userListPanel.add(userItem);
 userListPanel.add(Box.createVerticalStrut(5));
 }
 } else {
 JLabel noUserLabel = new JLabel("没有可转发的用户");
 noUserLabel.setAlignmentX(Component.LEFT_ALIGNMENT);
 userListPanel.add(noUserLabel);
 }

 // 添加滚动面板
 JScrollPane scrollPane = new JScrollPane(userListPanel);
 forwardDialog.add(scrollPane, BorderLayout.CENTER);

 // 添加按钮面板
 JPanel buttonPanel = new JPanel(new FlowLayout(FlowLayout.RIGHT));
 JButton cancelButton = new JButton("取消");
 cancelButton.addActionListener(e -> forwardDialog.dispose());
 buttonPanel.add(cancelButton);

 forwardDialog.add(buttonPanel, BorderLayout.SOUTH);

 // 显示对话框
 forwardDialog.setVisible(true);
}

/**
 * 创建用户项
 */
private JPanel createUserItem(User user, String content, BiConsumer<String, User> forwardCallback, JDialog dialog) {
 JPanel userItem = new JPanel(new BorderLayout());
 userItem.setBorder(BorderFactory.createEmptyBorder(5, 0, 5, 0));
 userItem.setBackground(Color.WHITE);

 // 创建用户头像和名称
 JPanel userInfoPanel = new JPanel(new BorderLayout(10, 0));
 userInfoPanel.setOpaque(false);

 // 创建头像
 JLabel avatar = createUserAvatar(user.getName().substring(0, 1).toUpperCase(), 30);
 userInfoPanel.add(avatar, BorderLayout.WEST);

 // 创建用户名
 JLabel nameLabel = new JLabel(user.getName());
 nameLabel.setFont(textFont);
 userInfoPanel.add(nameLabel, BorderLayout.CENTER);

 userItem.add(userInfoPanel, BorderLayout.CENTER);

 // 创建转发按钮
 JButton forwardButton = new JButton("转发");
 forwardButton.addActionListener(e -> {
 if (forwardCallback != null) {
 forwardCallback.accept(content, user);
 }
 dialog.dispose();
 });
 userItem.add(forwardButton, BorderLayout.EAST);

 // 添加鼠标悬停效果
 userItem.addMouseListener(new MouseAdapter() {
 @Override
 public void mouseEntered(MouseEvent e) {
 userItem.setBackground(new Color(245, 245, 245));
 }

 @Override
 public void mouseExited(MouseEvent e) {
 userItem.setBackground(Color.WHITE);
 }

 @Override
 public void mouseClicked(MouseEvent e) {
 forwardButton.doClick();
 }
 });

 return userItem;
}
````

最后，我们需要在 `MainView` 类中实现转发功能：

````java path=src/main/java/com/example/view/MainView.java mode=EDIT
// 在初始化方法中添加转发监听器
private void initChatPanel() {
 // 其他初始化代码...

 // 设置转发消息监听器
 chatPanel.setForwardMessageListener((content, isOwnMessage) -> {
 // 获取在线用户列表
 List<User> forwardUsers = new ArrayList<>(onlineUsers.values());

 // 从列表中移除当前用户
 forwardUsers.removeIf(user -> user.getId() == currentUser.getId());

 // 显示转发对话框
 chatPanel.showForwardDialog(content, forwardUsers, (messageContent, targetUser) -> {
 // 执行转发操作
 forwardMessage(messageContent, targetUser);
 });
 });
}

/**
 * 转发消息给指定用户
 * @param content 消息内容
 * @param targetUser 目标用户
 */
private void forwardMessage(String content, User targetUser) {
 if (out != null) {
 // 发送私聊消息格式: PM:接收者ID:消息内容
 String pmCommand = "PM:" + targetUser.getId() + ":" + content;
 out.println(pmCommand);

 // 显示转发成功提示
 chatPanel.addSystemMessage("已转发消息给 " + targetUser.getName());

 // 如果已有与该用户的私聊窗口，也在窗口中显示消息
 if (privateChatWindows.containsKey(targetUser.getId())) {
 privateChatWindows.get(targetUser.getId()).sendMessage(content);
 }
 }
}
````

同样，我们需要在 `PrivateChatView` 类中添加转发功能：

````java path=src/main/java/com/example/view/PrivateChatView.java mode=EDIT
// 在初始化方法中添加转发监听器
private void initChatPanel() {
 // 其他初始化代码...

 // 设置转发消息监听器
 chatPanel.setForwardMessageListener((content, isOwnMessage) -> {
 // 获取可转发的用户列表
 // 这里需要从主窗口获取用户列表，可以通过回调或其他方式实现
 List<User> forwardUsers = getForwardableUsers();

 // 显示转发对话框
 chatPanel.showForwardDialog(content, forwardUsers, (messageContent, targetUser) -> {
 // 执行转发操作
 forwardMessage(messageContent, targetUser);
 });
 });
}

/**
 * 获取可转发的用户列表
 * @return 用户列表
 */
private List<User> getForwardableUsers() {
 // 这里应该从主窗口获取用户列表
 // 临时实现，实际应用中需要修改
 List<User> users = new ArrayList<>();

 // 添加当前聊天对象，如果不是当前用户
 if (targetUser.getId() != currentUser.getId()) {
 users.add(targetUser);
 }

 return users;
}

/**
 * 转发消息给指定用户
 * @param content 消息内容
 * @param targetUser 目标用户
 */
private void forwardMessage(String content, User targetUser) {
 if (out != null) {
 // 发送私聊消息格式: PM:接收者ID:消息内容
 String pmCommand = "PM:" + targetUser.getId() + ":" + content;
 out.println(pmCommand);

 // 显示转发成功提示
 chatPanel.addSystemMessage("已转发消息给 " + targetUser.getName());
 }
}
````

这些修改添加了以下功能：

1. 在 `MessageBubble` 类中添加了转发功能的接口和方法
2. 在右键菜单中添加了"转发"选项
3. 在 `ChatPanel` 类中添加了转发消息监听器接口和实现
4. 添加了显示转发对话框的方法，用于选择转发对象
5. 在 `MainView` 和 `PrivateChatView` 类中实现了转发功能

这样，用户就可以通过右键菜单或其他方式转发消息了。转发功能会显示一个对话框，让用户选择要转发的对象，然后将消息发送给选定的用户。
